import cv2 import numpy as np import matplotlib.pyplot as plt
def canny(image: np.ndarray) -> np.ndarray: """ Applies Canny edge detection to the input image.
Parameters: image (np.ndarray): Input image in RGB format.
Returns: np.ndarray: Image with edges detected. """ gray_image = cv2.cvtColor(image, cv2.COLOR_RGB2GRAY) smooth_image = cv2.GaussianBlur(gray_image, (5, 5), 0) canny_edges = cv2.Canny(smooth_image, 50, 150) return canny_edges
def main(): image = cv2.imread("media/test_image.jpg") canny_edges = canny(image) plt.imshow(canny_edges) plt.show()
if __name__ == "__main__": main()
